#include <iostream>using namespace std;int main(){	setlocale (LC_ALL, "rus");	int n;	cout<< "¬ведите n: ";	cin >> n;	while (n <= 2)	{		cout << "n должен быть больше 2 ";		cout << "¬ведите n: ";		cin >> n;	}	if (n > 1) {		int x = n - 1;		int y = 1;		for (int i = 1; i <= n; i++)		{			for (int j = 1; j <= x; j++)				cout << " ";			x--;			for (int k = 1; k <= y; k++)				cout << "*";			y++;			cout << "\n";	}	return(0);}